function cleanFolder(folderName) {
    return fileOrFolderExists(folderName)
        .then(function (exists) {
        if (exists) {
            return deleteDir(folderName)
                .then(function () { return mkdirRecursive(folderName); });
        }
        else {
            return mkdirRecursive(folderName);
        }
    });
}
