InputFileResolver.prototype.resolve = function () {
    var _this = this;
    return new Promise(function (resolve, reject) {
        var errors = [];
        Promise.all([InputFileResolver.resolveFileGlobs(_this.mutateFileExpressions), InputFileResolver.resolveFileGlobs(_this.allFileExpressions)])
            .then(function (files) {
            var mutateFiles = files[0];
            var allFiles = files[1];
            fileUtils_1.normalize(allFiles);
            fileUtils_1.normalize(mutateFiles);
            mutateFiles.forEach(function (mutateFile) {
                if (allFiles.indexOf(mutateFile) < 0) {
                    errors.push("Could not find mutate file \"" + mutateFile + "\" in list of files.");
                }
            });
            if (errors.length > 0) {
                reject(errors);
            }
            else {
                resolve(allFiles.map(function (file) { return { path: file, shouldMutate: mutateFiles.some(function (mutateFile) { return mutateFile === file; }) }; }));
            }
        }, function (error) { return reject(error); });
    });
};
