InputFileResolver.resolveFileGlob = function (expression) {
    var _this = this;
    return fileUtils_1.glob(expression).then(function (files) {
        if (files.length === 0) {
            _this.reportEmptyGlobbingExpression(expression);
        }
        fileUtils_1.normalize(files);
        return files;
    });
};
